import React from "react";
import styled from "styled-components";
import IPhone from "./iPhone/iPhone";

const Main = () => {

    window.addEventListener('scroll', () => {
        document.body.style.setProperty('--scroll', window.pageYOffset / (document.body.offsetHeight - window.innerHeight));
      }, false);
    return (
        <MainContainer>
            <MainLeftSide>
                <h1>Цифровое решение для вашего бизнеса</h1>
                <h1>Полный цикл разработки</h1>
                <h1>Уникальные решения</h1>
                <OurProjects style={{animationDelay:`calc(var(--scroll) * -1s)`}}>Наши проекты</OurProjects>
            </MainLeftSide>
            <MainRightSide>
                <IPhone bgcolor={'white'} />
            </MainRightSide>
        </MainContainer>
    )
}

const MainContainer = styled.div`
    width: 70%;
    min-height: 500vh;
    margin: 90px auto;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
`

const MainLeftSide = styled.div`
    width: 35%;
`

const OurProjects = styled.h1`
    position: relative;
    top: 50%;
    animation: mainPage 1s linear infinite;
    animation-play-state: paused;
    /* animation-delay: calc(var(--scroll) * -1s); */

    animation-iteration-count: 1;
    animation-fill-mode: both;
`

const MainRightSide = styled.div`
    position: fixed;
    top: 90px;
    right: 15%;
`

export default Main
